mapper.select:
SELECT 

FROM eam_syslink a

WHERE a.id = #{id}

SELECT 

FROM eam_syslink a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.link_type = #{linkType}


		AND a.link_status = #{linkStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM eam_syslink a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_syslink SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
