mapper.select:
SELECT 

FROM eam_system_module a

WHERE a.id = #{id}

SELECT
FROM eam_system_module a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.eam_system_id = #{eamSystemId}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.chinese_name LIKE '%'||#{chineseName}||'%' '%'+#{chineseName}+'%' concat('%',#{chineseName},'%')
AND a.manager = #{manager.id}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM eam_system_module a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_system_module SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
