mapper.select:
SELECT 

FROM eam_system a

WHERE a.id = #{id}

SELECT 

FROM eam_system a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.eam_sysgroup_id = #{eamSysgroupId}


		AND a.eam_aa_layer_id = #{eamAaLayerId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM eam_system a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_system SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
