mapper.select:
SELECT 

FROM eam_system a

WHERE a.id = #{id}

SELECT 

FROM eam_system a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name = #{name}


		AND a.chinese_name LIKE 
			'%'||#{chineseName}||'%'
			'%'+#{chineseName}+'%'
			concat('%',#{chineseName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM eam_system a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_system SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
