mapper.select:
SELECT 

FROM eam_linknode a

WHERE a.id = #{id}

SELECT 

FROM eam_linknode a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.eam_system_id = #{eamSystemId}


		AND a.linknode_type = #{linknodeType}


		AND a.eam_protocol_id = #{eamProtocolId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM eam_linknode a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_linknode SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
