mapper.select:
SELECT 

FROM eam_node_timeout_re a

WHERE a.id = #{id}

SELECT 

FROM eam_node_timeout_re a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.eam_linknode_id = #{eamLinknodeId}


		AND a.eam_timeout_alg_id = #{eamTimeoutAlgId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM eam_node_timeout_re a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_node_timeout_re SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
