mapper.select:
SELECT 

FROM eam_sysproperty a

WHERE a.id = #{id}

SELECT 

FROM eam_sysproperty a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.id = #{id}


		AND a.eam_sysprop_type_id = #{eamSyspropTypeId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM eam_sysproperty a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_sysproperty SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
