mapper.select:
SELECT 

FROM eam_system_sysproperty_re a

WHERE a.id = #{id}

SELECT 

FROM eam_system_sysproperty_re a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.eam_system_id = #{eamSystemId}


		AND a.eam_sysproperty_id = #{eamSyspropertyId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM eam_system_sysproperty_re a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_system_sysproperty_re SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
