mapper.select:
SELECT 

FROM eam_aa_strategy a

WHERE a.id = #{id}

SELECT 

FROM eam_aa_strategy a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.eam_system_id = #{eamSystemId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM eam_aa_strategy a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE eam_aa_strategy SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
